import { Redirect } from "react-router-dom"
import {useState} from 'react'
import Header from './Header'
import Axios from 'axios';
export default function Users(props){
    let [row,setRow]=useState([]);
    const handleClick = (event, param) => {
        
        Axios.post("http://localhost:3002/api/delete",{"id":param})
      };
    
    
    if(!props.authorized){
        return <Redirect to="/"/>
    }

    

   
    Axios.get("http://localhost:3002/api/get").then((response)=>setRow(response.data))
    let idrow=row.map((res)=>{
        return(
            <tr >
            <th>{res.nom}</th>
            <th>{res.prenom}</th>
            <th>{res.email}</th>
            <th>{res.password}</th>
            <th><button className="users--button">res</button></th>
            <th><button className="users--button" onClick={event => handleClick(event, res.id)}>Supprimer</button></th>
            </tr>
        )
    })
   
    return (
    <>
    <Header/>
    
    
    <table className="user--table   ">
    <tbody>
        <tr>
            <th>NOM</th>
            <th>PRENOM</th>
            <th>EMAIL</th>
            <th>PASSWORD</th>
            <th>ADMIN</th>
            <th>DELETE</th>
        </tr>
       {idrow}
       <tr>
      
        
       </tr>
    </tbody>
    </table>
    
    
    </>
    )
}
